import { Component } from '@angular/core';
import { FormBuilder,FormGroup,FormControl } from '@angular/forms';
import { student_type } from '../student_interface';
import { SServiceService } from '../s-service.service';


@Component({
  selector: 'app-stu-form',
  templateUrl: './stu-form.component.html',
  styleUrls: ['./stu-form.component.css'],
  providers:[SServiceService]
})
export class StuFormComponent {


  data:Array<student_type>=[];

  deletedata(id:number){
    console.log("d");
    this.st.deldata(id);
  }

  adddata(){
    this.st.datapush(this.student_form.value);
    this.data=this.st.datashow();
    // this.data.push(this.student_form.value);
    console.log(this.data);
  }

  update(obj:student_type){
    this.data.pat

  }

  student_form!:FormGroup;
  constructor(private fb:FormBuilder,private st:SServiceService){
    this.student_form = this.fb.group({

      Id:[''],
      name:[''],
      class:[''],
      email:['']

    })

  }


}
<div class="container">

  <form [formGroup]="student_form" (submit)="adddata()">


    <div class="form-group">
      <label for="id">ID:</label>
      <input type="text" name="id" id="id" placeholder="e.g. 1" formControlName="Id">

    </div>
    <div class="form-group">
      <label for="name">Name:</label>
      <input type="text" name="name" id="name" placeholder="e.g. vikas sonwane" formControlName="name">

    </div>
    <div class="form-group">
      <label for="class">Class:</label>
      <input type="text" name="class" id="class" placeholder="e.g. 10" formControlName="class">

    </div>
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="text" name="email" id="email" placeholder="e.g. abc@gnail.com" formControlName="email">

    </div>

    <button class="btn btn-primary">submit</button>
  </form>
</div>

<div class="container">
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Class</th>
        <th>Email</th>

      </tr>
    </thead>
    <tbody>
<tr *ngFor="let i of data,let x=index">
  <td>{{i.Id}}</td>
  <td>{{i.name}}</td>
  <td>{{i.class}}</td>
  <td>{{i.email}}</td>
  <td><button type="button" (click)="deletedata(x)">delete</button></td>
  <td><button type="button" (click)="deletedata(x)">update</button></td>


</tr>
    </tbody>
  </table>
</div>


<p>
  {{data|json}}
</p>
