import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../service/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {



  registerform!:FormGroup;
  constructor(private auth:AuthService,private fb:FormBuilder,private route:route){




  }

  ngOnInit(): void {


    this.registerform=this.fb.group({
      Uname:['',[Validators.required,Validators.pattern('^[a-zA-Z]+$')]],
      Email:['',[Validators.required,Validators.email]],


      Phone_No:['',[Validators.required,Validators.maxLength(10),Validators.pattern('^\d{1,10}$')]],
      Password:['',[Validators.required,Validators.maxLength(8)]]
    })

  }




  submit(){
    console.log(this.registerform.value);
    this.auth.register(this.registerform.value).subscribe(x=>{
      console.log(x);
      console.log("user created")

    })
  }
}
