import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';
import { FormBuilder,FormGroup,Validators } from '@angular/forms';

@Component({
  selector: 'app-productview',
  templateUrl: './productview.component.html',
  styleUrls: ['./productview.component.css']
})
export class ProductviewComponent  implements OnInit{

  productlist!:any;

  editproduct!:any;

  editform!:any;

  constructor(private api:ApiService,private fb:FormBuilder){



  }

ngOnInit(): void {

  this.api.getlist().subscribe((x)=>{
    console.log(x);
    this.productlist=x;

  });



}

edit(id:number,index:number){
  alert(id);
 var data=this.productlist[index];
 console.log(data)

 this.editproduct=data;
 console.log(data);


 this.editform=this.fb.group({
  pid:['this.editform.pid',[Validators.required]],
  Pname: [this.editform.pname, [Validators.required]],
  description: [this.editform.description, [Validators.required, Validators.maxLength(100)]],
  Price: [this.editform.price, [Validators.required, Validators.pattern('')]],
  Qty: [this.editform.qty, [Validators.required, Validators.pattern('')]],
  Photo: [this.editform.photo, [Validators.required]]



})

}


delete(id:number){
  alert(id);
}



submiteditform(){

  console.log(

    this.editform.value
    );
}

}
<div class="container">
  <app-navbar> </app-navbar>

  <!-- {{ productlist | json }} -->

  <div class="row mt-5">
    <ng-container *ngFor="let x of productlist,let i as index ">

      <div class="col col-sm-4 col-md-3 d-flex">
        <div class="card">
        <img src="{{ x.photo }}" class="card-img-top" alt="{{ x.photo }}" />
        <div class="card-body">
          <h5 class="card-title">{{ x.pname }}</h5>
          <h6 class="card-subtitle mb-2 text-muted">{{ x.price }}</h6>
          <button class="btn btn-warning m-2" (click)="edit(x.pid,i)" data-bs-toggle="modal" data-bs-target="#exampleModal">Edit</button>
          <button class="btn btn-danger m-2" (click)="delete(x.pid)">Delete</button>
        </div>
      </div>
    </div>
  </ng-container>
  </div></div>



<div>


</div>



<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Edit Details</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
