import { Component, OnInit } from '@angular/core';
import { AuthService } from '../service/auth.service';
import { FormBuilder,Validators,FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {


  loginform!:FormGroup;
  constructor(private auth:AuthService,private fb:FormBuilder){




  }



  ngOnInit(): void {


    this.loginform=this.fb.group({
      Email:['',[Validators.required,Validators.email]],
      Password:['',[Validators.required,Validators.maxLength(8)]]
    })



  }


  submit(){
    console.log(this.loginform.value);

    this.auth.login(this.loginform.value).subscribe((x=>{

      console.log(object);
    }))
  }

}
<div class="container mt-5 w-25">

<form (ngSubmit)="submit()" [formGroup]="loginform">



  <div class="form-group">



    <label for="email">Email:</label>
    <input type="email" name="email" id="email" class="form-control" formControlName="Email">
  </div>




  <div class="form-group">



    <label for="">Password:</label>
    <input type="password" name="password" id="password" class="form-control" formControlName="Password" maxlength="8">
  </div>

  <br>
  <p>
    <a routerLink="/signup" class="fw-bold text-decoration-none text-dark">Not have an Account?</a>
  </p>

  <div class="form-group mt-3">

    <button type="submit" class="w-100 btn btn-primary">Login</button>
  </div>
</form>
</div>
