import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';

@Component({
  selector: 'app-productview',
  templateUrl: './productview.component.html',
  styleUrls: ['./productview.component.css']
})
export class ProductviewComponent  implements OnInit{

  productlist!:any;
  constructor(private api:ApiService){

  }

ngOnInit(): void {

  this.api.getlist().subscribe((x)=>{
    console.log(x);
    this.productlist=x;

  })
}

edit(id:number){
  alert(id);
}


delete(id:number){
  alert(id);
}


}
<div class="container">
  <app-navbar> </app-navbar>

  <!-- {{ productlist | json }} -->

  <div class="row mt-5">
    <ng-container *ngFor="let x of productlist">

      <div class="col col-sm-4 col-md-3 d-flex">
        <div class="card">
        <img src="{{ x.photo }}" class="card-img-top" alt="{{ x.photo }}" />
        <div class="card-body">
          <h5 class="card-title">{{ x.pname }}</h5>
          <h6 class="card-subtitle mb-2 text-muted">{{ x.price }}</h6>
          <button class="btn btn-warning m-2" (click)="edit(x.pid)">Edit</button>
          <button class="btn btn-danger m-2" (click)="delete(x.pid)">Delete</button>
        </div>
      </div>
    </div>
  </ng-container>
  </div></div>



<div>



<div class="modal fade" id="staticBackdrop" data-backdrop="static" data-keyboard="false" tabindex="-1" aria-labelledby="staticBackdrop" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">

      </div>

    </div>
  </div>
</div>



<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#">
  Launch demo modal
</button>
</div>
