import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';

@Component({
  selector: 'app-productview',
  templateUrl: './productview.component.html',
  styleUrls: ['./productview.component.css']
})
export class ProductviewComponent  implements OnInit{

  productlist!:any;
  constructor(private api:ApiService){

  }

ngOnInit(): void {

  this.api.getlist().subscribe((x)=>{
    console.log(x);
    this.productlist=x;

  })
}

edit(id:number){
  alert(id);
}


delete(id:number){
  alert(id);
}


}
<div class="container">
  <app-navbar> </app-navbar>

  <!-- {{ productlist | json }} -->

  <div class="row mt-5">
    <ng-container *ngFor="let x of productlist">

      <div class="col col-sm-4 col-md-3 d-flex">
        <div class="card">
        <img src="{{ x.photo }}" class="card-img-top" alt="{{ x.photo }}" />
        <div class="card-body">
          <h5 class="card-title">{{ x.pname }}</h5>
          <h6 class="card-subtitle mb-2 text-muted">{{ x.price }}</h6>
          <button class="btn btn-warning m-2" (click)="edit(x.pid)">Edit</button>
          <button class="btn btn-danger m-2" (click)="delete(x.pid)">Delete</button>
        </div>
      </div>
    </div>
  </ng-container>
  </div></div>



<div>


</div>



<!-- Button trigger modal -->
<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
  Launch demo modal
</button>

<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
