import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../service/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent implements OnInit {
  registerform!: FormGroup;
  constructor(
    private auth: AuthService,
    private fb: FormBuilder,
    private route: Router
  ) {}

  ngOnInit(): void {
    this.registerform = this.fb.group({
      Uname: ['', [Validators.required, Validators.pattern('^[a-zA-Z]+$')]],
      Email: ['', [Validators.required, Validators.email]],

      Phone_No: [
        '',
        [
          Validators.required,
          Validators.maxLength(10),
          Validators.pattern('^d{1,10}$'),
        ],
      ],
      Password: ['', [Validators.required, Validators.maxLength(8)]],
    });
  }

  submit() {
    console.log(this.registerform.value);
    this.auth.register(this.registerform.value).subscribe(
      (x) => {
        console.log(x);
        console.log('user created');
        this.route.navigateByUrl('/login');
      }
    );
  }
}
<div class="container mt-5 w-25">

  <form (ngSubmit)="submit()" [formGroup]="registerform">




    <div class="form-group">



      <label for="Uname">Username:</label>
      <input type="Uname" name="Uname" id="Uname" class="form-control" formControlName="Uname">
    </div>


    <div class="form-group">



      <label for="email">Email:</label>
      <input type="email" name="email" id="email" class="form-control" formControlName="Email">
    </div>


    <div class="form-group">



      <label for="Phone_No">Phone_No:</label>
      <input type="Phone_No" name="Phone_No" id="Phone_No" class="form-control" formControlName="Phone_No" maxlength="10">
    </div>




    <div class="form-group">



      <label for="">Password:</label>
      <input type="password" name="password" id="password" class="form-control" formControlName="Password" maxlength="8">
    </div>

    <br>
    <p>

      <a routerLink="/login" class="fw-bold text-decoration-none text-dark">Alredy have an Account?</a>
    </p>


    <div class="form-group mt-3">

      <button type="submit" class="w-100 btn btn-primary">Signup</button>
    </div>
  </form>
  </div>
