import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';

@Component({
  selector: 'app-productview',
  templateUrl: './productview.component.html',
  styleUrls: ['./productview.component.css']
})
export class ProductviewComponent  implements OnInit{

  productlist!:any;
  constructor(private api:ApiService){

  }

ngOnInit(): void {

  this.api.getlist().subscribe((x)=>{
    console.log(x);
    this.productlist=x;

  })
}

edit(id:number){
  alert(id);
}


delete(id:number){
  alert(id);
}


}
<div class="container">
  <app-navbar> </app-navbar>

  {{ productlist | json }}

  <div *ngFor="let x of productlist" >
    <div class="col col-sm-4">
      <div class="card">
        <img src="{{ x.photo }}" class="card-img-top" alt="{{ x.photo }}" />
        <div class="card-body">
          <h5 class="card-title">{{ x.pname }}</h5>
          <p class="card-text">{{ x.description }}</p>
          <h6 class="card-subtitle mb-2 text-muted">{{ x.price }}</h6>
          <button class="btn btn-warning" (click)="edit(x.pid)">Edit</button>
          <button class="btn btn-danger" (click)="delete(x.pid)">Delete</button>
        </div>
      </div>
    </div>
  </div>
</div>
