import { Component, OnInit } from '@angular/core';
import { ApiService } from '../service/api.service';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-addproducts',
  templateUrl: './addproducts.component.html',
  styleUrls: ['./addproducts.component.css'],
})
export class AddproductsComponent implements OnInit {
  addproduct!: FormGroup;

  categories!: any;
  subcategories!: any;

  constructor(private api: ApiService, private fb: FormBuilder) {}

  getsubcat(catid: any) {
    let id =parseInt(catid.value);


    this.api.getsubcategories(id).subscribe((x) => {
      console.log(x);
      this.subcategories = x;
    });
  }

  ngOnInit(): void {
    this.api.getcategories().subscribe((x) => {
      console.log(x);
      this.categories = x;
    });

    this.addproduct = this.fb.group({
      Pname: ['', [Validators.required]],
      Pdescription: ['', [Validators.required, Validators.maxLength(100)]],
      Price: ['', [Validators.required, Validators.pattern('')]],
      Qty: ['', [Validators.required, Validators.pattern('')]],
      Photo: ['', [Validators.required]],
      Category: ['', [Validators.required]],

      Subcategory: ['', [Validators.required]],
    });
  }

  submit() {
    console.log(this.addproduct.value);
  }
}
<div class="container">
  <app-navbar> </app-navbar>
</div>

  <div class="container mt-5 w-25">
    <form (ngSubmit)="submit()" [formGroup]="addproduct">
      <div class="form-group">
        <label for="Pname">Product name:</label>
        <input
          type="text"
          name="Pname"
          id="Pname"
          class="form-control"
          formControlName="Pname"
        />
      </div>


      <div class="form-group">
        <label for="Pdescription">Product description:</label>
        <input
          type="text"
          name="Pdescription"
          id="Pdescription"
          class="form-control"
          formControlName="Pdescription"
        />
      </div>

      <div class="form-group">
        <label for="Category">Category:</label>
        <select name="Category" id="Category" formControlName="Category" class="form-control" (change)="getsubcat($event.target)">
          <ng-container *ngFor="let x of categories">
            <option value="{{x.id}}">{{x.category}}</option>
          </ng-container>
        </select>
      </div>

      <div class="form-group">
        <label for="Subcategory">Subcategory:</label>
        <select name="Subcategory" id="Subcategory" formControlName="Subcategory" class="form-control">
          <ng-container ng>
            <option value=""></option>
          </ng-container>
        </select>
      </div>


      <div class="form-group">
        <label for="Photo">Photo:</label>
        <input
          type="file"
          name="Photo"
          id="Photo"
          class="form-control"
          formControlName="Photo"
        />
      </div>

      <div class="form-group">
        <label for="Qty">Qty:</label>
        <input
          type="number"
          name="Qty"
          id="Qty"
          class="form-control"
          formControlName="Qty"
          min="1"
        />
      </div>

      <div class="form-group">
        <label for="Price">Price:</label>
        <input
          type="text"
          name="Price"
          id="Price"
          class="form-control"
          formControlName="Price"
        />
      </div>


      <div class="form-group mt-3">
        <button type="submit" class="w-100 btn btn-primary">Add Product</button>
      </div>
    </form>
  </div>


