import { Component, OnInit } from '@angular/core';
import { FormGroup,FormBuilder,FormControl, Validators } from '@angular/forms';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  constructor(private fb:FormBuilder,private auth:AuthService){

  }

  registerform!:FormGroup;

  ngOnInit(): void {

    this.registerform= this.fb.group({

    Username:['',[Validators.required]],
     Email:['',[Validators.required,Validators.email]],
       Password:['',[Validators.required,Validators.maxLength(8)]],
      Phoneno:['',[Validators.required,Validators.maxLength(10)]]
    })

  }


  submitform(){

    console.log(this.registerform.value)

    this.auth.register(this.registerform.value).subscribe(x=>{
      console.log(x);
      console.log("register succefully");
    })

  }
}



<div class="container mt-5 w-25">



  <form [formGroup]="registerform" (ngSubmit)="submitform()">


    <div class="formGroup">
      <label for="Username">Email</label>
      <input type="Username" id="Username" class="form-control" formControlName="Username">
    </div>


    <div class="formGroup">
      <label for="Email">Email</label>
      <input type="email" id="Email" class="form-control" formControlName="Email">
    </div>



    <div class="formGroup">
      <label for="Phoneno">Phoneno</label>
      <input type="Phoneno" id="Phoneno" class="form-control" formControlName="Phoneno">
    </div>

    <div class="formGroup">
      <label for="Password">Password</label>
      <input type="password" id="Password" class="form-control" formControlName="Password">
    </div>


    <br>
    <br>
    <button type="submit">submit</button>
  </form>

</div>
